""" Rotazione piede LLeg e rotazione busto:    NAO senza staccare i piedi da terra ruota esternamente LLeg, attende qualche secondo,e poi ruota il busto nella    direzione che ha assunto il piede LLeg"""import motionimport timefrom lib import stiffness_ondef rotation_foot_l_leg(motion_proxy, posture_proxy, tts_proxy):    # Set NAO in Stiffness On    stiffness_on(motion_proxy)    # Send NAO to Pose Init    posture_proxy.goToPosture("StandInit", 0.3)    motion_proxy.wbEnable(True)    # Legs/Feet configuration    stateName = "Fixed"    supportLeg = "RLeg"    motion_proxy.wbFootState(stateName, supportLeg)    stateName = "Plane"    supportLeg = "LLeg"    motion_proxy.wbFootState(stateName, supportLeg)    # Cartesian foot trajectory    # Warning: Needs a PoseInit before executing    space = motion.FRAME_ROBOT    axisMask = 63  # control all the effector's axes    isAbsolute = False    # Lower the Torso and move to the side    effector = "Torso"    path = [0.0, 0.0, 0.15, 0.0, 0.0, 0.05]    timeList = 3.0  # seconds    motion_proxy.positionInterpolation(effector, space, path, axisMask, timeList, isAbsolute)    motion_proxy.wbEnable(False)    time.sleep(3)  # waiting a few seconds    motion_proxy.wbEnable(True)    # New Legs/Feet configuration    stateName = "Fixed"    supportLeg = "LLeg"    motion_proxy.wbFootState(stateName, supportLeg)    stateName = "Plane"    supportLeg = "RLeg"    motion_proxy.wbFootState(stateName, supportLeg)    posture_proxy.goToPosture("StandInit", 0.25)    motion_proxy.wbEnable(False)